mapper.select:
select sum(menu_Point) from cart c left join menu m on m.menu_id = c.menu_id where user_id = #{userId} and m.menu_id in
#{cartArr}
group by user_id

mapper.update:

		update users set user_price = user_price + #{totalPay} , user_grade = 
		 case 
  		  when user_price + #{totalPay} > 200000 then 'gold'
   		  when user_price + #{totalPay} > 100000 then 'silver'
  		  else 'bronze' end,
		user_point = user_point - #{point} + #{getPoint}
		where user_id = #{userId}
